import React, { useState } from "react";

const StateTutorial = () => {
  const [inputValue, setInputValue] = useState("Pedro");
  // const [Value, setValue] = useState("test");

 

  return (
    <div>
      <input placeholder="enter something..." name="inputValue" value={inputValue} onChange={(e) => setInputValue(e.target.value)} />
      {/* <input placeholder="enter something..." name="Value" value={Value} onChange={onChange}/> */}
      <p>{inputValue}</p>
      {/* <p>{Value}</p> */}
    </div>
  );
};

export default StateTutorial;
